import {useState} from 'react';
import Board from './Board';

const Game = () => {
  const [history, setHistory] = useState(
    [
      {
        squares: Array(9).fill(null),
        point: null
      }
    ]
  );
  const [stepNumber, setStepNumber] = useState(0);
  const [xIsNext, setXIsNext] = useState(true);
  const [movesOrder, setMovesOrder] = useState(false);

  const handleClick = (i) => {
    const copyHistory = history.slice(0, stepNumber + 1);
    const current = copyHistory[copyHistory.length - 1];
    const squares = current.squares.slice();
    if (calculateWinner(squares).winner || squares[i]) {
      return;
    }
    squares[i] = xIsNext ? "X" : "O";
    setHistory(
      copyHistory.concat(
        [
          {
            squares: squares,
            point: i
          }
        ]
      )
    );
    setStepNumber(copyHistory.length);
    setXIsNext(!xIsNext);
  };

  const jumpTo = (step) => {
    setStepNumber(step);
    setXIsNext((step % 2) === 0);
  };

  const current = history[stepNumber];
  const {winner, winLine, isDraw} = calculateWinner(current.squares);
  const moves = history.map((step, move) => {
    const col = step.point % 3 + 1;
    const row = (step.point / 3 + 1) | 0;
    const goToMove = 'Go to move #' + move + '(' + col + ', ' + row + ')';
    const desc = move ? goToMove : 'Go to game start';
    return (
      <li key={move}>
        <button
          onClick={() => jumpTo(move)}
          className={move === stepNumber ? 'bold' : ''}
        >
          {desc}
        </button>
      </li>
    );
  });

  if (movesOrder) {
    moves.reverse();
  }

  let status;
  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xIsNext ? "X" : "O");
  }
  if (isDraw) {
    status = 'Draw';
  }

  return (
    <div className="game">
      <div className="game-board">
        <Board
          squares={current.squares}
          onClick={i => handleClick(i)}
          winLine={winLine}
        />
      </div>
      <div className="game-info">
        <div>{status}</div>
        <button onClick={() => {setMovesOrder(!movesOrder)}}>
          {'ASK⇔DESK'}
        </button>
        <ol>{moves}</ol>
      </div>
    </div>
  );
};

const calculateWinner = (squares) => {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  const result = {
    winner: null,
    winLine: [],
    isDraw: false
  }
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      result.winner = squares[a];
      result.winLine = result.winLine.concat(lines[i]);
    }
  }
  if (result.winner === null && !squares.includes(null)) {
    result.isDraw = true;
  }
  return result;
};

export default Game;
